import React from "react";
import { useNavigate } from 'react-router-dom';  // For navigation

const BlogSections = () => {
  const navigate = useNavigate();  // Initialize useNavigate hook

  // Define the click handler functions
  const openMuseumDetails = () => {
    navigate("/museum-details"); // Navigate to the museum details page
  };

  const open365DaysDetails = () => {
    navigate("/365-days-details"); // Navigate to the 365 days details page
  };

  const openFetchFestivalDetails = () => {
    navigate("/fetch-festival-details"); // Navigate to the fetch festival details page
  };

  return (
    <section className="FrontImg2-Block">
      <div className="cnt-3">
        <BlogCard
          imgSrc="/images/84770f_c65865dcbe344e959c00cd572c022bfd~mv2.webp"
          title="20 MUSEUMS YOU SHOULD VISIT AT LEAST ONCE"
          text="See the full program for the upcoming festival."
          onClick={openMuseumDetails}
        />
        <BlogCard
          imgSrc="/images/84770f_fe6fab9a56414a00976c0d1673796c90~mv2.webp"
          title="365 DAYS. 365 FRAMES."
          text="Shots from the same location in Oslo for one year."
          onClick={open365DaysDetails}
        />
        <BlogCard
          imgSrc="/images/84770f_fa58299371f84ccab29edc0d417d1ac3~mv2.webp"
          title="FETCH FESTIVAL BRLN 2023"
          text="The full list includes 10 cities in 8 countries."
          onClick={openFetchFestivalDetails}
        />
      </div>
    </section>
  );
};

const BlogCard = ({ imgSrc, title, text, onClick }) => {
  return (
    <div className="MOVE1">
      <img src={imgSrc} alt={title} className="FrontImg2" />
      <button className="button_gui" onClick={onClick}>
        <h1 className="cnt0">{title}</h1>
      </button>
      <p className="ct0">{text}</p>
    </div>
  );
};

export default BlogSections;
