import React, { useState } from 'react';
import './Comment.css';
const Comment = () => {
  // State to store the name, comment, and the list of comments
  const [commenterName, setCommenterName] = useState('');
  const [commentText, setCommentText] = useState('');
  const [comments, setComments] = useState([]);

  // Handler to add a new comment
  const addComment = () => {
    if (commenterName && commentText) {
      const newComment = {
        commenter_name: commenterName,
        comment_text: commentText,
      };
      
      // Update the comments list with the new comment
      setComments((prevComments) => [...prevComments, newComment]);
      
      // Clear the input fields
      setCommenterName('');
      setCommentText('');
    } else {
      alert('Please fill in both fields');
    }
  };

  return (
    <div className="comment-section">
      <h2>Leave a Comment</h2>
      
      {/* Input for commenter's name */}
      <input
        type="text"
        placeholder="Your Name"
        value={commenterName}
        onChange={(e) => setCommenterName(e.target.value)}
      />
      
      {/* Input for comment text */}
      <input
        type="text"
        placeholder="Your Comment"
        value={commentText}
        onChange={(e) => setCommentText(e.target.value)}
      />
      
      {/* Button to post the comment */}
      <button onClick={addComment}>Post Comment</button>

      <h3>Comments:</h3>
      {comments.length === 0 ? (
        <p>No comments yet</p>
      ) : (
        <ul className="comment-list">
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.commenter_name}:</strong> {comment.comment_text}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Comment;
