}

function openRelax() {
    window.open("", "_blank");
}

function openEat() {
    window.open("", "_blank");
}

// Register content popup with proper link element
function openreg() {
    fetch('content-1.html')
    .then(response => response.text())
    .then(htmlContent =>  {
        let newWindow = window.open("","_blank");
        newWindow.document.write(htmlContent);

        let linkElement = newWindow.document.createElement('link');
        linkElement.rel = 'stylesheet';
        linkElement.type = 'text/css';
        linkElement.href = 'register.css';

        newWindow.document.head.appendChild(linkElement);
    })
    .catch(error => console.error('Error fetching document:', error));
}

// Comment-related functions
function addComment() {
    var blog_id = document.getElementById("blog_id").value;
    var commenter_name = document.getElementById("commenter_name").value;
    var comment_text = document.getElementById("comment_text").value;

    fetch("/add_comment", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            "blog_id": blog_id,
            "commenter_name": commenter_name,
            "comment_text": comment_text
        })
    })
    .then(response => {
        if (!response.ok) {
            throw new Error("Network response was not ok");
        }
        return response.json();
    })
    .then(data => {
        console.log(data.message); // Handle success
    })
    .catch(error => {
        console.error("There was a problem with your fetch operation:", error);
    });
}

function getComments(blog_id) {
    fetch(`/get_comments/${blog_id}`)
    .then(response => {
        if (!response.ok) {
            throw new Error("Network response was not ok");
        }
        return response.json();
    })
    .then(data => {
        console.log(data); // Handle success, update UI with comments
    })
    .catch(error => {
        console.error("There was a problem with your fetch operation:", error);
    });
}

function submitComment() {
    var comment = document.getElementById('comment').value;
    fetch('/submit_comment', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ comment: comment }),
    })
    .then(response => response.json())
    .then(data => {
        alert(data.message); // Handle success message
    })
    .catch((error) => {
        console.error('Error:', error); // Handle error
    });
}
