import React from 'react';
import BlogPost from './components/BlogPost'; // Ensure BlogPost is defined correctly

const posts = [
    {
        date: "Jun 12, 2023",
        readTime: 2,
        title: "365 DAYS. 365 FRAMES",
        summary: "The hamburg-based artist opens the door to a different dimension.",
        image: "Public/images/23.webp", // Ensure this path is correct
        content: "Welcome to your blog post. Use this space to connect with your readers...",
        designQuote: "Do you have a design in mind for your blog?...",
        content2: "Every layout comes with the latest social features...",
        content3: "You'll be posting loads of engaging content...",
        content4: "Blogging gives your site a voice, so let your business' personality shine through.",
        stunQuote: "Be original, show off your style, and tell your story.",
        inspiration: "To keep up with all things, including website building tips...",
    },
    // You can add more posts to this array as needed
];

const App = () => {
    return (
        <div>
            <h1 className="he11">We`blog</h1>
            {posts.map((post, index) => (
                <BlogPost key={index} post={post} /> // Rendering each BlogPost component
            ))}
        </div>
    );
};

export default App;
