// src/components/BlogPost.js
import React from 'react';

const BlogPost = ({ post }) => {
    return (
        <div className="blog-post">
            <h2>{post.title}</h2>
            <p>{post.date} â€¢ {post.readTime} min read</p>
            <img src={post.image} alt={post.title} />
            <p>{post.summary}</p>
            <div className="blog-content">
                <p>{post.content}</p>
                <p>{post.designQuote}</p>
                <p>{post.content2}</p>
                <p>{post.content3}</p>
                <p>{post.content4}</p>
                <p><strong>{post.stunQuote}</strong></p>
                <p>{post.inspiration}</p>
            </div>
        </div>
    );
};

export default BlogPost;
