import React, { useState } from 'react';
import './index.css';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from './AuthContext';
import { useLocation } from 'react-router-dom';

function App() {
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [commenterName, setCommenterName] = useState('');
  const [blogId, setBlogId] = useState('');

  const openLink = (url) => {
    window.open(url, "_blank");
  };

  const fetchContent = (contentUrl, cssFile) => {
    fetch(contentUrl)
      .then(response => response.text())
      .then(htmlContent => {
        let newWindow = window.open("", "_blank");
        newWindow.document.write(htmlContent);
        let linkElement = newWindow.document.createElement('link');
        linkElement.rel = 'stylesheet';
        linkElement.type = 'text/css';
        linkElement.href = cssFile;
        newWindow.document.head.appendChild(linkElement);
      })
      .catch(error => console.error('Error fetching document:', error));
  };

  // Comment submission
  const addComment = () => {
    fetch("/add_comment", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        blog_id: blogId,
        commenter_name: commenterName,
        comment_text: commentText
      })
    })
      .then(response => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then(data => {
        console.log(data.message);
      })
      .catch(error => {
        console.error("There was a problem with your fetch operation:", error);
      });
  };

  const getComments = (blog_id) => {
    fetch(`/get_comments/${blog_id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then(data => {
        setComments(data);
      })
      .catch(error => {
        console.error("There was a problem with your fetch operation:", error);
      });
  };

  return (
    <div className="App">
      <div className="top-bar">
        <div className="h">
          <h1>We`blog</h1>
        </div>
        <div id="input3">
          <button type="button" onClick={() => openLink('/register')}>Register</button>
          <button type="button" onClick={() => openLink('/login')}>Log in</button>
        </div>
      </div>

      <div id="Tab-bar">
        <div className="Search1">
          <section className="search-button">
            <input type="text" id="input" placeholder="Search..." />
            <button type="button"><i className="fa fa-search"></i></button>
          </section>
        </div>

        <div className="mainbuttons">
          <button className="b1" type="button" onClick={() => openLink('/')}>Home</button>
          <button className="b1" type="button" onClick={() => openLink('/about')}>About</button>
          <button className="b1" type="button">Travel</button>
          <button className="b1" type="button">Relax</button>
          <button className="b1" type="button">Eat</button>
        </div>

        <div className="fa">
          <button className="b2" type="button" onClick={() => openLink('https://www.instagram.com/')}><i className="fa fa-instagram"></i></button>
          <button className="b2" type="button" onClick={() => openLink('https://www.pinterest.com/')}><i className="fa fa-pinterest"></i></button>
          <button className="b2" type="button" onClick={() => openLink('https://www.facebook.com/')}><i className="fa fa-facebook"></i></button>
          <button className="b2" type="button" onClick={() => openLink('https://twitter.com/')}><i className="fa fa-twitter"></i></button>
        </div>
      </div>

      <div className="imgcontainer">
        <img src="images/Denmark-GettyImages-587892190.webp" alt="Front" className="FrontImg1" />
        <div className="imgcontent">
          <h2>Travel Blogs</h2>
          <h5>Exploring destinations. Not everywhere yet, </h5>
          <h5>but it's on my agenda.</h5>
        </div>
      </div>

      <div className="ct">
        <p>I'm a paragraph. Click here to add your own text and edit me...</p>
      </div>

      <section className="FrontImg2-Block">
        <div className="cnt-3">
          <div className="MOVE1">
            <img src="images/84770f_c65865dcbe344e959c00cd572c022bfd~mv2.webp" alt="Side" className="FrontImg2" />
            <button className="button_gui" onClick={() => fetchContent('/museums', '/styles/museum.css')}>
              <h1 className="cnt0">20 MUSEUMS YOU SHOULD VISIT AT LEAST ONCE</h1>
            </button>
            <p className="ct0"> See the full program for the upcoming festival...</p>
          </div>
          <div className="MOVE2">
            <img src="images/84770f_fe6fab9a56414a00976c0d1673796c90~mv2.webp" alt="Side" className="FrontImg2" />
            <button className="button_gui" onClick={() => fetchContent('/frames', '/styles/frames.css')}>
              <h1 className="cnt1">365 DAYS. 365 FRAMES.</h1>
            </button>
            <p className="ct1">Shots from the same location in Oslo...</p>
          </div>
          <div className="MOVE3">
            <img src="images/84770f_fa58299371f84ccab29edc0d417d1ac3~mv2.webp" alt="Side" className="FrontImg2" />
            <button className="button_gui" onClick={() => fetchContent('/festival', '/styles/festival.css')}>
              <h1 className="cnt2"> FETCH FESTIVAL BRLN 2023</h1>
            </button>
            <p className="ct2">The full list includes 10 cities in 8 different countries.</p>
          </div>
        </div>
      </section>

      <div className="qt">
        <h1 className="ch0"> As Featured in</h1>
        <h4 className="ch2">Travel blog</h4>
        <h4 className="ch3">Plane tour</h4>
        <h4 className="ch4">World travel</h4>
        <h4 className="ch5">skycloud</h4>
        <h4 className="ch6">Personal Blogs</h4>
      </div>

      <div className="FrontImg3-Block">
        <img src="./images/84770f_6ead7869ff5a4842aee87b0d58d83504~mv2.webp" alt="Front" className="FrontImg3" />
        <div className="dt">
          <h3> STORY OF THE WEEK </h3>
          <h6>Jun 12, 2023 • 2 min</h6>
          <button type="button" onClick={() => fetchContent('/story', '/styles/story.css')}>
            <h2>THE STORY BEHIND “FYI: I’M ABOUT TO LOVE YOU” - AN INTERVIEW WITH KAY VAN HANS</h2>
          </button>
        </div>
      </div>

      <div className="Arena">
        <div className="line-above"></div>
        <div className="ted">Destination Guides</div>
        <div className="line-below"></div>
      </div>

      <section className="Destination">
        <div className="Destination_guide">
          <div className="destination-item">
            <img src="images/Bangkok_The Siam. Cafe Cha 8.webp" alt="Bangkok" />
            <button className="button_gui">Bangkok Travel Guide</button>
            <p>When in doubt, get a tuk-tuk</p>
          </div>
          <div className="destination-item">
            <img src="images/London.webp" alt="London" />
            <button className="button_gui">London Travel Guide</button>
            <p>Where the pubs are as old as the monuments</p>
          </div>
          <div className="destination-item">
            <img src="images/Hero_Haarkon_Paris_Musee+Bourdelle.webp" alt="Paris" />
            <button className="button_gui">Paris Travel Guide</button>
            <p>From rooftops to romance</p>
          </div>
        </div>
      </section>

      <h2>Leave a Comment</h2>
      <input
        type="text"
        placeholder="Your Name"
        value={commenterName}
        onChange={(e) => setCommenterName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Your Comment"
        value={commentText}
        onChange={(e) => setCommentText(e.target.value)}
      />
      <button onClick={addComment}>Post Comment</button>

      <h3>Comments:</h3>
      {comments.length === 0 ? (
        <p>No comments yet</p>
      ) : (
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.commenter_name}:</strong> {comment.comment_text}
            </li>
          ))}
        </ul>
      )}

      <script src="index.js"></script>
    </div>
  );
}

export default App;
