import React, { useState } from 'react';
import './index.css'
function App() {
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [commenterName, setCommenterName] = useState('');
  const [blogId, setBlogId] = useState('');


  const openLink = (url) => {
    window.open(url, "_blank");
  };

  const fetchContent = (contentUrl, cssFile) => {
    fetch(contentUrl)
      .then(response => response.text())
      .then(htmlContent => {
        let newWindow = window.open("", "_blank");
        newWindow.document.write(htmlContent);
        let linkElement = newWindow.document.createElement('link');
        linkElement.rel = 'stylesheet';
        linkElement.type = 'text/css';
        linkElement.href = cssFile;
        newWindow.document.head.appendChild(linkElement);
      })
      .catch(error => console.error('Error fetching document:', error));
  };

  // Comment submission
  const addComment = () => {
    fetch("/add_comment", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        blog_id: blogId,
        commenter_name: commenterName,
        comment_text: commentText
      })
    })
    .then(response => {
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      return response.json();
    })
    .then(data => {
      console.log(data.message);
    })
    .catch(error => {
      console.error("There was a problem with your fetch operation:", error);
    });
  };

  const getComments = (blog_id) => {
    fetch(`/get_comments/${blog_id}`)
    .then(response => {
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      return response.json();
    })
    .then(data => {
      setComments(data); // Assuming response contains an array of comments
    })
    .catch(error => {
      console.error("There was a problem with your fetch operation:", error);
    });
  };

  return (
    <div className="App">
      <h1>Social Links</h1>
      <button onClick={() => openLink("https://www.instagram.com/")}>Instagram</button>
      <button onClick={() => openLink("https://www.pinterest.com/")}>Pinterest</button>
      <button onClick={() => openLink("https://www.facebook.com/")}>Facebook</button>
      <button onClick={() => openLink("https://X.com/")}>Twitter</button>

      <h2>Fetch and Open Content</h2>
      <button onClick={() => fetchContent('contents.html', 'Intern.css')}>Open Contents</button>
      <button onClick={() => fetchContent('content-1.html', 'Intern.css')}>Open Content 1</button>

      <h2>Comments Section</h2>
      <input
        type="text"
        placeholder="Blog ID"
        value={blogId}
        onChange={(e) => setBlogId(e.target.value)}
      />
      <input
        type="text"
        placeholder="Your Name"
        value={commenterName}
        onChange={(e) => setCommenterName(e.target.value)}
      />
      <textarea
        placeholder="Write your comment..."
        value={commentText}
        onChange={(e) => setCommentText(e.target.value)}
      ></textarea>
      <button onClick={addComment}>Submit Comment</button>

      <h3>Comments for Blog {blogId}</h3>
      <button onClick={() => getComments(blogId)}>Load Comments</button>
      <ul>
        {comments.map((comment, index) => (
          <li key={index}>{comment.commenter_name}: {comment.comment_text}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
