import React, { useState } from 'react';
import './index.css'
function App() {
  const [comments, setComments] = useState([]);
  const [commentText, setCommentText] = useState('');
  const [commenterName, setCommenterName] = useState('');
  const [blogId, setBlogId] = useState('');


  const openLink = (url) => {
    window.open(url, "_blank");
  };


  return (
    <div className="App">
      <h1>Social Links</h1>
      <button onClick={() => openLink("https://www.instagram.com/")}>Instagram</button>
      <button onClick={() => openLink("https://www.pinterest.com/")}>Pinterest</button>
      <button onClick={() => openLink("https://www.facebook.com/")}>Facebook</button>
      <button onClick={() => openLink("https://X.com/")}>Twitter</button>


      <h2>Comments Section</h2>
      <input
        type="text"
        placeholder="Blog ID"
        value={blogId}
        onChange={(e) => setBlogId(e.target.value)}
      />
      <input
        type="text"
        placeholder="Your Name"
        value={commenterName}
        onChange={(e) => setCommenterName(e.target.value)}
      />
      <textarea
        placeholder="Write your comment..."
        value={commentText}
        onChange={(e) => setCommentText(e.target.value)}
      ></textarea>
