      <h3>Comments for Blog {blogId}</h3>
      <button onClick={() => getComments(blogId)}>Load Comments</button>
      <ul>
        {comments.map((comment, index) => (
          <li key={index}>{comment.commenter_name}: {comment.comment_text}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
