import React, { useEffect, useState } from 'react';
import './style.css';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

import Destinations from './components/Destinations';
import Slide from './components/Slide';
import Block from './components/Block';
import Story from './components/Story';
import Featured from './components/Featured';
import Explore from './components/Explore';
import Footer from './components/Footer';

function App() {
  const openLink = (url) => {
    window.open(url, '_blank');
  };

  const [navbarOpen, setNavbarOpen] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => setLoading(false), 2000); 

    return () => {};
  }, []);

  return (
    <div className="App">
      <div id="loading-screen" className={loading ? 'show' : 'hide'}>
        <div className="loading-container">
          <div className="loading-circle"></div>
        </div>
      </div>
      <nav className="top-bar">
        <div className="h">
          <h1>We`blog</h1>
        </div>
        <button className="nav-toggle" onClick={() => setNavbarOpen(!navbarOpen)}>
          {navbarOpen ? <i className="fa fa-times"></i> : <i className="fa fa-bars"></i>}
        </button>
        <div className={`nav-menu ${navbarOpen ? 'show' : ''}`}>
          <div className="mainbuttons">
            <button className="b1" onClick={() => openLink('/')}>Home</button>
            <button className="b1" onClick={() => openLink('/about')}>About</button>
            <button className="b1">Travel</button>
            <button className="b1">Relax</button>
            <button className="b1">Eat</button>
          </div>
        </div>
      </nav>
      <Router>
        <Routes>
          <Route exact path="/" component={Destinations} />
          <Route path="/about" component={Story} />
          <Route path="/blog" component={Destinations} />
        </Routes>
      </Router>
      <Slide />
      <Block />
      <Featured />
      <Explore />
      <Footer />
    </div>
  );
}

export default App;
