import React from 'react';
import { BrowserRouter as Router, Route, Routes, NavLink } from 'react-router-dom';
import './App.css';
import { Header, SearchBar, FeatureImage, BlogSections, Footer } from './components';
import Register from './components/Register';
import Login from './components/Login';
import NotFound from './components/NotFound'; // New 404 component

function App() {
  return (
    <Router>
      <div className="App">
        <Header />

        <div className="hero-section">
          <div className="hero-content">
            <h1>Welcome to We`Blog</h1>
            <p>Your daily source of insightful content</p>
            <div className="cta-buttons">
              <NavLink to="/register" className="cta-button">Join Us</NavLink>
              <NavLink to="/login" className="cta-button">Login</NavLink>
            </div>
          </div>
        </div>

        <SearchBar />
        <FeatureImage />
        <BlogSections />

        {/* Routing */}
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route path="*" element={<NotFound />} /> {/* 404 page for unmatched routes */}
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
