function addComment() {
    var blog_id = document.getElementById("blog_id").value;
    var commenter_name = document.getElementById("commenter_name").value;
    var comment_text = document.getElementById("comment_text").value;

    fetch("/add_comment", {
        method: "POST",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify({
            "blog_id": blog_id,
            "commenter_name": commenter_name,
            "comment_text": comment_text
        })
    })
    .then(response => {
        if (!response.ok) {
            throw new Error("Network response was not ok");
        }
        return response.json();
    })
    .then(data => {
        // Handle success response
        console.log(data.message);
    })
    .catch(error => {
        // Handle error
        console.error("There was a problem with your fetch operation:", error);
    });
}

function getComments(blog_id) {
    fetch(`/get_comments/${blog_id}`)
    .then(response => {
        if (!response.ok) {
