import React, { useState } from 'react';

import './Footer.css';

const Footer = () => {

    const [commenterName, setCommenterName] = useState('');
  const [commentText, setCommentText] = useState('');
  const [comments, setComments] = useState([]);


  const addComment = () => {
    if (commenterName && commentText) {
      const newComment = {
        commenter_name: commenterName,
        comment_text: commentText,
      };


      setComments((prevComments) => [...prevComments, newComment]);


      setCommenterName('');
      setCommentText('');
    } else {
      alert('Please fill in both Fields');
    }
  };
    return (
        <div className='footer'>
            
            <div className="comment-section">
      <h2>Leave a Comment</h2>

      <input type="text" placeholder="Your Name" value={commenterName}
        onChange={(e) => setCommenterName(e.target.value)}/>

      <input
        type="text"
        placeholder="Your Comment"
        value={commentText}
        onChange={(e) => setCommentText(e.target.value)}
      />


      <button onClick={addComment}>Post Comment</button>

      <h3>Comments ~</h3>
      {comments.length === 0 ? (
        <p>No comments yet</p>
      ) : (
        <ul className="comment-list">
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.commenter_name}: <i> {comment.comment_text}</i></strong>
            </li>
          ))}
        </ul>
      )}
    </div>
               <div class="footer-content">
        <div class="footer-left">
            <h3>Contact Us On</h3>
            <p>Email: <a href="mailto:weblog@outlook.com">weblog@outlook.com</a></p>
            <p>Phone: +91 9080970408</p>
            <p>Address: Hello Home, Mansison</p>
        </div>
        <div class="footer-center">
            <h3>Follow Us On</h3>
            <div class="social-icons">
                <button type="button" onclick="instalink()"><a class="fa fa-instagram"></a></button>
                <button type="button" onclick="pinlink()"><a class="fa fa-pinterest"></a></button>
                <button type="button" onclick="facelink()"><a class="fa fa-facebook"></a></button>
            </div>
        </div>

    </div>
    <div class="footer-bottom">
        &copy; 2024 We`Blog. All rights reserved.
    </div>
    </div>
 
    );
}
export default Footer;
